name = "app_sync_users" description = "always be in sync with UCS users" filter = "(&(uid=*)(!(uid=*$))" attributes = ["givenName", "sn", "mailPrimaryAddress"]
def handler(dn, new, old): if new and not old: add_user(new)
if not new and old: remove_user(old)
if new and old: modify_user(new, old)
def add_user(new): new_mailPrimaryAddress = new.get(['mailPrimaryAddress', [])[0] new_givenName = new.get(['givenName', [])[0] new_sn = new.get(['sn', [])[0] get_db_connection().add(new_givenName, new_sn, new_mailPrimaryAddress)
def remove_user(old): old_mailPrimaryAddress = old.get(['mailPrimaryAddress', [])[0] get_db_connection().remove(old_mailPrimaryAddress)
def modify_user(new, old): old_mailPrimaryAddress = old.get(['mailPrimaryAddress', [])[0] new_mailPrimaryAddress = new.get(['mailPrimaryAddress', [])[0] new_givenName = new.get(['givenName', [])[0] new_sn = new.get(['sn', [])[0] get_db_connection().modify(old_mailPrimaryAddress, new_givenName, new_sn, new_mailPrimaryAddress)
def get_db_connection(): raise NotImplementedError()
